package _3datatypes;

public class TypeCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double a=45.78;
		int b=(int)a;
		System.out.println(b);
		
		int c=130;
		byte d=(byte)c;
		System.out.println(d);
		
		long h=987654321;
		int g=(int)h;
		short f=(short)h;
		byte y=(byte)h;
		System.out.print(g+ " "+ f+ " "+y);
		

	}

}
