package _4strings;

public class ConcatMethod {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("hoi helo" + "kui");
        System.out.println("my fav number is " + 5);
        System.out.println("my fav number is " + 5 + 3);
        System.out.println("my fav number is " + (5 + 3));
        System.out.println("helo".concat(" morning"));
        System.out.println("helo".concat(" afternoon").concat("5"));
	}

}
