package _9loops;
public class Dowhile1 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=-111;
		do {
			System.out.println(a);
			a++;
		}
		while(a<=-101);
		
		System.out.println("hui");
		int b=5;
		do {
			System.out.println(b);
			b--;
		}
		while(b>=-5);
		
		int c=-10;
		do {
			System.out.println(c);
			c++;
		}
		while(c<=0);
	}
}
