package _9loops;

public class For1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1111; i<=1121; i++) {
			System.out.println(i);
		}
		System.out.println("kus");
		for(int i=1234; i>=1223; i--) {
			System.out.println(i);
		}
		for(int i=-1111; i>=-1121; i--) {
			System.out.println(i);
		}
		for(int i=-1234; i<=-1223; i++) {
			System.out.println(i);
		}

	}

}
