package _9loops;

public class Forloop2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(char i='d'; i<='j'; i++) {
			System.out.print(i);
		}
		System.out.println("");
		for(char i='m'; i>='e'; i--) {
			System.out.print(i);
		}
		System.out.println("");
		for(char i='z'; i>='t'; i--) {
			System.out.print(i);
		}
		System.out.println("");
		for(char i='r'; i<='x'; i++) {
			System.out.print(i);
		}

	}

}
