package _9loops;

public class While2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=-123;
		while(a>=-129) {
			System.out.println(a);
			a--;
		}
		System.out.println("end");
		int b=79;
		while(b<=84) {
			System.out.println(b);
			b++;
		}
		int c=-110;
		while(c<=-100) {
			System.out.println(c);
			c++;
		}
		int d=0;
		while(d>=-9) {
			System.out.println(d);
			d--;
		}
	}

}
