package _9loops;
public class Whileloop3 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char a='A';
		while(a<='M') {
			System.out.print(a);
			a++;
		}
		System.out.println("");
		char b='x';
		while(b>='m') {
			System.out.print(b);
			b--;
		}
	}
}
